import { Component, OnInit, AfterViewInit } from '@angular/core';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

@Component({
  selector: 'app-animated-section',
  templateUrl: './animated-section.component.html',
  styleUrls: ['./animated-section.component.css']
})
export class AnimatedSectionComponent implements OnInit, AfterViewInit {

  constructor() { }

  ngOnInit(): void { }

  ngAfterViewInit(): void {
    this.initScrollAnimations();
  }

  initScrollAnimations() {
    gsap.from('.title', {
      scrollTrigger: {
        trigger: '.title',
        start: 'top 80%', // déclencher l'animation quand le haut de l'élément atteint 80% de la hauteur de la fenêtre
      },
      opacity: 0,
      y: 50,
      duration: 1
    });

    gsap.from('.content', {
      scrollTrigger: {
        trigger: '.content',
        start: 'top 80%', // déclencher l'animation quand le haut de l'élément atteint 80% de la hauteur de la fenêtre
      },
      opacity: 0,
      y: 50,
      duration: 1,
      delay: 0.3
    });
  }
}
