// animated-images.component.ts
import { Component, OnInit, AfterViewInit } from '@angular/core';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

@Component({
  selector: 'app-animated-images',
  templateUrl: './animated-images.component.html',
  styleUrls: ['./animated-images.component.css']
})
export class AnimatedImagesComponent implements OnInit, AfterViewInit {

  images = [
    { src: 'assets/images/image1.jpg', alt: 'Image 1' },
    { src: 'assets/images/image2.jpg', alt: 'Image 2' },
    { src: 'assets/images/image3.jpg', alt: 'Image 3' },
    // Ajoutez plus d'images ici
  ];

  animations = ['fadein', 'fadeinleft', 'fadeinright', 'zoomin', 'flipleft', 'flipup', 'scalein'];

  constructor() { }

  ngOnInit(): void { }

  ngAfterViewInit(): void {
    this.initScrollAnimations();
  }

  initScrollAnimations() {
    this.images.forEach((_, i) => {
      gsap.fromTo(`.animate-${i % this.animations.length}`, {
        opacity: 0,
        y: 50,
        x: i % 2 === 0 ? -50 : 50
      }, {
        scrollTrigger: {
          trigger: `.animate-${i % this.animations.length}`,
          start: 'top 80%'
        },
        opacity: 1,
        y: 0,
        x: 0,
        duration: 1
      });
    });
  }
}
