import { Component, OnInit } from '@angular/core';
import { FortniteService } from '../fortnite.service';

@Component({
  selector: 'app-cosmetics-list',
  templateUrl: './cosmetics-list.component.html',
  styleUrls: ['./cosmetics-list.component.css']
})
export class CosmeticsListComponent implements OnInit {
  cosmetics: any[] = []; // Initialisation avec un tableau vide

  constructor(private fortniteService: FortniteService) { }

  ngOnInit(): void {
    this.fetchCosmetics();
  }

  fetchCosmetics(): void {
    this.fortniteService.getAllCosmetics()
      .subscribe(data => {
        this.cosmetics = data.data; // Assignation des données récupérées depuis l'API
        console.log(this.cosmetics); // Pour le débogage, vous pouvez retirer cette ligne en production
      }, error => {
        console.error('Error fetching cosmetics:', error);
      });
  }
}
<div *ngIf="cosmetics">
    <h2>Liste des Cosmétiques</h2>
    <ul>
      <li *ngFor="let cosmetic of cosmetics">
        {{ cosmetic.name }} - {{ cosmetic.type }} - {{ cosmetic.rarity }}
      </li>
    </ul>
  </div>
