// app.component.ts
import { Component, OnInit } from '@angular/core';
import AOS from 'aos';
import 'aos/dist/aos.css';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  ngOnInit() {
    AOS.init();
  }
}
<!-- app.component.html -->
<nav class="navbar navbar-expand-lg ">
  <div class="container-fluid">
    <a class="navbar-brand mx-auto" href="#">
      <img src="../assets/images/logo.png" alt="Logo" style="height: 40px;">
    </a>
  </div>
</nav>

<div class="video-container">
  <video class="video-fullscreen" src="../assets/videos/fortniteavatar.mp4" autoplay muted loop></video>
</div>


<div class="container mt-4 p-3">
  <h2>Texte après la vidéo</h2>
  <p>Votre texte ici...</p>
  <p>Plus de texte ici...</p>
</div>

<app-animated-images></app-animated-images>

<div class="flex flex-column align-items-center gap-2">
  <span class="text-xl font-medium">Scroll Down</span>
  <span class="slidedown-icon h-2rem w-2rem bg-primary border-circle inline-flex align-items-center justify-content-center">
    <i class="pi pi-arrow-down"></i>
  </span>
</div>
